import React from 'react'
import styled from 'styled-components'

function ChatMessage() {
    return (
        <Container>
            <UserAvatar>
                <img src="https://randomuser.me/api/portraits/men/11.jpg"/>
            </UserAvatar>
            <MessageContent>
                <Name>
                    Ramon Perry
                    <span>25/2/2021 11:13:55 AM</span>
                </Name>
                <Text>
                    Hi this is Ramon
                </Text>
            </MessageContent>
        </Container>
    )
}

export default ChatMessage

const Container = styled.div`
    padding: 8px 20px;
    display: flex;

`
const UserAvatar = styled.div`
    width: 36px;
    height: 36px;
    border-radius: 2px;
    overflow: hidden;
    margin-right

    img {
        width:100%;
    }
`
const MessageContent = styled.div`
    display:flex;
    flex-direction: column;
`
