import React from 'react'
import styled from 'styled-components'

function ChatMessage({ text, name, userImage, timeStamp }) {
    return (
        <Container>
            <UserAvatar>
                <img src="https://randomuser.me/api/portraits/men/11.jpg"/>
            </UserAvatar>
            <MessageContent>
                <Name>
                    {name}
                    <span>{timeStamp}</span>
                </Name>
                <Text>
                    Hi this is Ramon
                </Text>
            </MessageContent>
        </Container>
    )
}

export default ChatMessage

const Container = styled.div`
    padding: 8px 20px;
    display: flex;
    align-items: center;

    :hover {
        background: black;
    }

`
const UserAvatar = styled.div`
    width: 36px;
    height: 36px;
    border-radius: 2px;
    overflow: hidden;
    margin-right: 8px;

    img {
        width:100%;
    }
`
const MessageContent = styled.div`
    display:flex;
    flex-direction: column;
`
const Name = styled.div`
    font-weight: 900;
    font-size: 15px;
    line-height: 1.4;
    span {
        font-weight: 400;
        color: rgb(97,96,97);
        margin-left: 8px;
        font-size: 13px;
    }
`
const Text = styled.div`

`
