import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined'
import { useParams } from 'react-router-dom'

import ChatInput from '../components/ChatInput'
import ChatMessage from './ChatMessage'
import db from '../firebase'

function Chat() {

    const [ channel, setChannel ] = useState()

    const getMessages = () => {
        db.collection('rooms')
    }

    let { channelId } = useParams();

    const getChannel = () => {
        db.collection('rooms')
        .doc(channelId)
        .onSnapshot((snapshot) => {
            setChannel(snapshot.data())
        })
    }

    useEffect(() => {
        getChannel()
    }, [channelId])

    return (
        <Container>
            <Header>
                <Channel>
                    <ChannelName>
                        # {channel.name}
                    </ChannelName>
                    <ChannelInfo>
                        companu-wide annocuments and work realted matters
                    </ChannelInfo>
                </Channel>
                <ChannelDetails>
                    <div>
                        Details
                    </div>
                    <Info />
                </ChannelDetails>
            </Header>
            <MessageContainer>
                <ChatMessage/>
            </MessageContainer>
            <ChatInput/>
        </Container>
    )
}

export default Chat

const Container = styled.div`
    display: grid;
    grid-template-rows: 64px auto min-content;
`
const Header = styled.div`
    padding-left: 20px;
    padding-right: 20px;
    display: flex;
    align-items: center;
    border-bottom: 1px solid rgb(83, 39, 13);
    justify-content: space-between;
`
const Channel = styled.div`

`
const ChannelName = styled.div`
    font-weight: 700;
`
const ChannelInfo = styled.div`
    font-weight: 400;
    color: #606060;
    font-size: 13px;
    margin-top: 8px;
`
const ChannelDetails = styled.div`
    display: flex;
    align-items: center;
    color: #606060;
`
const Info = styled(InfoOutlinedIcon)`
    margin-left: 10px;
`

const MessageContainer = styled.div`
`
